                            </Text>
                        </View>
                    </View>
                </View>
            </View>
        )
        return (
            <FlatList
                scrollEnabled={false}
                data={oldPosts}
                keyExtractor={(item) => `${item.id}`}
                renderItem={renderItem}
                listKey={`oldPosts`}
